import { createRouter, createWebHistory } from 'vue-router'
import ClientView from '../views/ClientView.vue'

const routes = [
  {
    path: '/',
    name: 'client',
    component: ClientView,
    redirect: '/index',
    children: [
      {
        path: 'index',
        name: 'index-client',
        component: () => import('../components/client/IndexPage.vue'),
      },
      {
        path: 'hall/:sessionId',
        name: 'hall-client',
        component: () => import('../components/client/HallPage.vue'),
        props: true
      },
      {
        path: 'payment/:sessionId/:seatsId',
        name: 'payment-client',
        component: () => import('../components/client/PaymentPage.vue'),
        props: true
      },
    ],
  },
  {
    path: '/admin',
    name: 'admin',
    redirect: '/admin/login',
    component: () => import('../views/AdminView.vue'),
    children: [
      {
        path: 'login',
        name: 'login-admin',
        component: () => import('../components/admin/LoginPage.vue'),
      },
      {
        path: 'redactor',
        name: 'redactor-admin',
        component: () => import('../components/admin/RedactorPage.vue'),
        meta: { requiresAuth: true }
      },
    ]
  }
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})


router.beforeEach((to, from, next) => {
  console.log(to);
  console.log(from);
  // Если переход на роут "/login", проверяем наличие токена в Vuex
  if (to.path === '/login') {
    const isAuthenticated = !!store.state.token; // Проверяем токен в Vuex
    // Если токен есть, перенаправляем на домашнюю страницу
    if (isAuthenticated) {
      next('/');
    } else {
      next(); // Продолжаем нормальный переход на страницу входа
    }
  } else {
    next(); // Продолжаем нормальный переход на другие страницы
  }
});

export default router
