import { createRouter, createWebHistory } from 'vue-router'
import ClientView from '../views/ClientView.vue'
import store from '@/store'

const routes = [
  {
    path: '/',
    name: 'client',
    component: ClientView,
    redirect: '/index',
    children: [
      {
        path: 'index',
        name: 'index-client',
        component: () => import('../components/client/IndexPage.vue'),
      },
      {
        path: 'hall/:sessionId',
        name: 'hall-client',
        component: () => import('../components/client/HallPage.vue'),
        props: true
      },
      {
        path: 'payment/:sessionId/:seatsId',
        name: 'payment-client',
        component: () => import('../components/client/PaymentPage.vue'),
        props: true
      },
    ],
  },
  {
    path: '/admin',
    name: 'admin',
    redirect: '/admin/login',
    component: () => import('../views/AdminView.vue'),
    children: [
      {
        path: 'login',
        name: 'login-admin',
        component: () => import('../components/admin/LoginPage.vue'),
      },
      {
        path: 'redactor',
        name: 'redactor-admin',
        component: () => import('../components/admin/RedactorPage.vue'),
        meta: { requiresAuth: true },
        beforeEnter: requireAuth
      },
    ]
  }
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})

// router.beforeEach((to, from, next) => {
//   if (to.path === '/admin/redactor') {
//     const isAuthenticated = !!store.state.token;
//     if (isAuthenticated) {
//       next();
//     } else {
//       next('/admin/login');
//     }
//   } else {
//     next();
//   }
// });

const requireAuth = (to, from, next) => {

  const isAuthenticated = !!store.state.token;
  if (isAuthenticated()) {
    next(); // Если пользователь аутентифицирован, продолжаем навигацию
  } else {
    next('/admin/login'); // Если пользователь не аутентифицирован, перенаправляем на страницу входа
  }
};


export default router
