import { createRouter, createWebHistory } from 'vue-router'
import ClientView from '../views/ClientView.vue'
import store from '@/store'

const routes = [
  {
    path: '/',
    name: 'client',
    component: ClientView,
    redirect: '/index',
    children: [
      {
        path: 'index',
        name: 'index-client',
        component: () => import('../components/client/IndexPage.vue'),
      },
      {
        path: 'hall/:sessionId',
        name: 'hall-client',
        component: () => import('../components/client/HallPage.vue'),
        props: true
      },
      {
        path: 'payment/:sessionId/:seatsId',
        name: 'payment-client',
        component: () => import('../components/client/PaymentPage.vue'),
        props: true
      },
    ],
  },
  {
    path: '/admin',
    name: 'admin',
    redirect: '/admin/login',
    component: () => import('../views/AdminView.vue'),
    children: [
      {
        path: 'login',
        name: 'login-admin',
        component: () => import('../components/admin/LoginPage.vue'),
      },
      {
        path: 'redactor',
        name: 'redactor-admin',
        component: () => import('../components/admin/RedactorPage.vue'),
        meta: { requiresAuth: true }
      },
    ]
  }
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})

// router.beforeEach((to, from, next) => {
  // if(to.path === '/admin/redactor') {
    // const isAuthenticated = !!store.state.token; // Проверяем, есть ли токен в Vuex
    // console.log(isAuthenticated)
    // // Если маршрут требует аутентификации, но пользователь не аутентифицирован, перенаправляем на страницу входа
    // if (!isAuthenticated) {
    //   next('/admin/login');
    // } else {
    //   next(); // Продолжаем нормальный переход
    // }
  // }
// });

router.beforeEach((to, from, next) => {
  const isAuthenticated = localStorage.getItem('token') !== null; // Проверяем, есть ли токен в localStorage или Vuex

  // Если маршрут требует аутентификации, но пользователь не аутентифицирован, перенаправляем на страницу входа
  if (to.matched.some(record => record.meta.requiresAuth) && !isAuthenticated) {
    // next('/admin/login');
  } else {
    next(); // Продолжаем нормальный переход
  }
});

export default router
